category_title, category_sort, category_datetime, category_managerid, category_modelId, category_categoryid, category_smallimg, category_appid, category_description,
#{categoryTitle}, #{categorySort}, #{categoryDateTime}, #{categoryManagerId}, #{categoryModelId}, #{categoryCategoryId}, #{categorySmallImg}, #{categoryAppId}, #{categoryDescription}

mapper.update:
update category
category_title=#{categoryTitle}, category_sort=#{categorySort}, category_datetime=#{categoryDateTime}, category_managerid=#{categoryManagerId}, category_categoryid=#{categoryCategoryId}, category_smallimg=#{categorySmallImg}, category_description=#{categoryDescription},
where category_id = #{categoryId}

